CREATE TABLE public.cameras (id int4 GENERATED ALWAYS AS IDENTITY, camera_name text UNIQUE NOT NULL, make text, model text, serial_number text, purchase_date timestamptz, purchase_price money, product_url text, num_of_batteries int2, status text DEFAULT 'Available', remarks text, organization_id int4 NOT NULL, PRIMARY KEY (id), FOREIGN KEY (organization_id) REFERENCES public.organizations (id) ON UPDATE RESTRICT ON DELETE RESTRICT);
