CREATE TABLE public.projects (id int4 GENERATED ALWAYS AS IDENTITY, project_code varchar UNIQUE NOT NULL, project_name text NOT NULL, short_name text NOT NULL, objectives text, contact_person text, contact_email text, organization_id int4 NOT NULL, project_owner text, p_owner_email text, start_date timestamptz, end_date timestamptz, created_at timestamptz DEFAULT now(), created_by text, site_id int4, PRIMARY KEY (id), FOREIGN KEY (organization_id) REFERENCES public.organizations (id) ON UPDATE RESTRICT ON DELETE RESTRICT);
