CREATE TABLE public.locations (id int4 GENERATED ALWAYS AS IDENTITY, location_name text UNIQUE NOT NULL, x float4 NOT NULL, y float4 NOT NULL, remarks text, organization_id int4 NOT NULL, project_id int4 UNIQUE NOT NULL, PRIMARY KEY (id), FOREIGN KEY (organization_id) REFERENCES public.organizations (id) ON UPDATE RESTRICT ON DELETE RESTRICT, FOREIGN KEY (project_id) REFERENCES public.projects (id) ON UPDATE RESTRICT ON DELETE RESTRICT);
