CREATE TABLE public.subprojects (id int4 GENERATED ALWAYS AS IDENTITY, subproject_name text UNIQUE NOT NULL, description text, organization_id int4 NOT NULL, project_id int4 UNIQUE NOT NULL, PRIMARY KEY (id), FOREIGN KEY (organization_id) REFERENCES public.organizations (id) ON UPDATE RESTRICT ON DELETE RESTRICT, FOREIGN KEY (project_id) REFERENCES public.projects (id) ON UPDATE RESTRICT ON DELETE RESTRICT);
