CREATE TABLE public.upload_details (id int4 GENERATED ALWAYS AS IDENTITY, submits int2 NOT NULL, uploads int2, start_time timestamptz NOT NULL, end_time timestamptz, status text NOT NULL, project_id int4 NOT NULL, deployment_id int4 NOT NULL, uploaded_by int4 NOT NULL, PRIMARY KEY (id), FOREIGN KEY (deployment_id) REFERENCES public.deployments (id) ON UPDATE RESTRICT ON DELETE CASCADE);
