CREATE TABLE public.images (file_id uuid NOT NULL, deployment_id int4 NOT NULL, file_name text UNIQUE NOT NULL, date_taken timestamptz NOT NULL, image_type text, identified_by text, identified_at timestamptz, species_count int2, is_highlighted bool DEFAULT 'false', is_identified bool DEFAULT 'false', is_profiled bool DEFAULT 'false', is_approved bool DEFAULT 'false', remarks text, uploaded_by text, project_id int4 NOT NULL, upload_id int4, profiled_by text, profiled_at timestamptz, PRIMARY KEY (file_id), FOREIGN KEY (file_id) REFERENCES storage.files (id) ON UPDATE RESTRICT ON DELETE CASCADE, FOREIGN KEY (deployment_id) REFERENCES public.deployments (id) ON UPDATE RESTRICT ON DELETE RESTRICT, FOREIGN KEY (project_id) REFERENCES public.projects (id) ON UPDATE RESTRICT ON DELETE RESTRICT, FOREIGN KEY (upload_id) REFERENCES public.upload_details (id) ON UPDATE RESTRICT ON DELETE RESTRICT);
