CREATE TABLE public.image_individuals (id int4 GENERATED ALWAYS AS IDENTITY, image_species_id int4 NOT NULL, age text DEFAULT 'Unknown', sex text DEFAULT 'Unknown', side text, individual_id int4, PRIMARY KEY (id), FOREIGN KEY (image_species_id) REFERENCES public.image_species (id) ON UPDATE RESTRICT ON DELETE CASCADE, FOREIGN KEY (individual_id) REFERENCES public.individuals (id) ON UPDATE RESTRICT ON DELETE RESTRICT);
