CREATE TABLE public.user_projects (id int4 GENERATED ALWAYS AS IDENTITY, user_id uuid UNIQUE NOT NULL, project_id int4 UNIQUE NOT NULL, is_p_revoked bool DEFAULT 'false' NOT NULL, user_role text NOT NULL, PRIMARY KEY (id), FOREIGN KEY (user_id) REFERENCES auth.users (id) ON UPDATE NO ACTION ON DELETE NO ACTION, FOREIGN KEY (project_id) REFERENCES public.projects (id) ON UPDATE CASCADE ON DELETE RESTRICT);
